mapper.select:
SELECT hm_order_child.* FROM hm_order_child,hm_order
WHERE  hm_order_child.order_id = hm_order.id  and
hm_order_child.order_state = 'reservation' AND
  hm_order_child.start_time >= #{startTime} and hm_order_child.start_time < #{endTime}
and  hm_order.hotel_id = #{hotelId};

SELECT * FROM hm_room
WHERE room_state = 'yes'
AND hotel_id = #{hotelId}

SELECT * FROM hm_commodity_transaction
WHERE
create_time >= #{startTime} and create_time < #{endTime}
AND hotel_id = #{hotelId}
and pay_type = 'cash';

SELECT * FROM hm_cashier_summary
WHERE
 create_time >= #{startTime} and create_time < #{endTime}
                                 AND project = '押金';

SELECT COUNT(1) FROM hm_order WHERE
hotel_id = #{hotelId}
AND
 check_time >= #{startTime} and check_time < #{endTime}
AND check_type = 'day'

SELECT * FROM hm_order
WHERE check_type = 'hour'
AND
check_time >= #{startTime} and check_time < #{endTime}
AND hotel_id = #{hotelId};

SELECT *  FROM hm_order
WHERE hotel_id = #{hotelId}
AND
check_time >= #{startTime} and check_time < #{endTime}
and members_id is not null;

SELECT *  FROM hm_order
   WHERE hotel_id = #{hotelId}
   AND
   check_time >= #{startTime} and check_time < #{endTime}
   AND  memberId_or_organizationId IS NOT NULL
   ;

SELECT * FROM hm_order
WHERE  hotel_id = #{hotelId}
AND memberId_or_organizationId = NULL
AND members_id = NULL
AND
check_time >= #{startTime} and check_time < #{endTime}

SELECT * FROM hm_order
WHERE order_type = 'directly'
and hotel_id = #{hotelId}
AND
 check_time >= #{startTime} and check_time < #{endTime}

SELECT COUNT(1) FROM hm_order_child WHERE order_id IN (SELECT id FROM hm_order WHERE hotel_id = #{hotelId}
AND
check_time >= #{startTime} and check_time < #{endTime}
 and members_id is not null )

 SELECT COUNT(1) FROM hm_order_child WHERE order_id IN (SELECT id FROM hm_order WHERE hotel_id = #{hotelId}
AND
check_time >= #{startTime} and check_time < #{endTime}
and  memberId_or_organizationId is not null )

SELECT COUNT(1) FROM hm_order_child WHERE order_id IN (SELECT id FROM hm_order WHERE hotel_id = #{hotelId}
  AND
   check_time >= #{startTime} and check_time < #{endTime}
   AND memberId_or_organizationId = NULL
   AND members_id = NULL )

SELECT COUNT(1) FROM hm_order_child WHERE order_Id IN
(SELECT id FROM hm_order WHERE  order_type = 'directly' AND hotel_id = #{hotelId} and
 check_time >= #{startTime} and check_time < #{endTime}
)

SELECT COUNT(1) FROM hm_order_child WHERE order_Id IN
(SELECT id FROM hm_order WHERE  order_type = 'subscribe' AND hotel_id = #{hotelId} and
check_time >= #{startTime} and check_time < #{endTime}
)

SELECT * FROM hm_order
WHERE order_type = 'subscribe'
AND hotel_id = #{hotelId}
AND
check_time >= #{startTime} and check_time < #{endTime}
