mapper.insert:
insert into hm_order_child set order_id=#{orderId}, order_state=#{orderState}, remark=#{remark}, start_time=#{startTime}, end_time=#{endTime},
room_id=#{roomId},
room_type_id=#{roomTypeId}, al_room_code=#{alRoomCode}

mapper.select:
select o.order_number,p.name,v.value,p.certificate_number,r.room_name, t.room_type_name,e.money,c.start_time,c.end_time,c.pay_cash_num,c.other_pay_num, c.room_rate,other_rate,c.free_rate_num from hm_order as o,hm_room as r,hm_room_type as t, hm_order_child as c,hm_dictionaries_value as v, hm_check_in_person as p,hm_everyday_room_price as e,hm_order_record as `or` where o.id=c.order_id and c.room_id=r.id and c.room_type_id=t.id and c.id=p.order_child_id and c.id=e.order_child_id and p.certificate_type=v.id and c.id=`or`.order_child_id and p.status='checkin' and (order_state='admissions')
and c.end_time>=#{afterTime}
and c.end_time<#{beforeTime}
and o.hotel_id=#{hotelId} group by or.order_child_id order by c.end_time

select o.order_number,p.name,v.value,p.certificate_number,r.room_name, t.room_type_name,e.money,c.start_time,c.end_time,c.pay_cash_num,c.other_pay_num, c.room_rate,other_rate,c.free_rate_num from hm_order as o,hm_room as r,hm_room_type as t, hm_order_child as c,hm_dictionaries_value as v, hm_check_in_person as p,hm_everyday_room_price as e,hm_order_record as `or` where o.id=c.order_id and c.room_id=r.id and c.room_type_id=t.id and c.id=p.order_child_id and c.id=e.order_child_id and p.certificate_type=v.id and c.id=`or`.order_child_id and p.status='checkin' and (order_state='admissions')
and c.end_time>=#{afterTime}
and c.end_time<#{beforeTime}
and o.hotel_id=#{hotelId} group by or.order_child_id order by c.end_time

select * from hm_order where
check_time <= #{date}
and (phone=#{mobile} or ID_number=#{idNumber})
and hotel_id=#{hotelId}
order by update_time desc
limit 1

select *
from hm_order_child
where order_id=#{id} and order_state=#{orderState}

select * from hm_order_child as oc,hm_check_in_person as cp
where oc.id=cp.order_child_id
and cp.status='checkin'
and order_state=#{orderState}
and oc.order_id=#{id}

select id from hm_order_child
where order_state='reservation'
and room_id=#{roomId}
and order_id=#{orderId}

select id from hm_order_child
where order_state='reservation'
and room_type_id=#{roomTypeId}
and room_id is null
and order_id=#{orderId}
limit 1

SELECT child.id ,der.check_time ,der.check_out_time ,room.room_name ,type.room_type_name ,
der.order_placer,der.phone ,der.order_type ,der.OTA ,der.channel,der.check_type,der.id as orderId ,
child.remark,child.order_state ,der.ID_number as certificateNumber
FROM hm_order der LEFT JOIN hm_order_child child ON der.id=child.order_id
INNER JOIN hm_room_type type ON type.id=child.room_type_id LEFT JOIN hm_room room ON room.id=child.room_id

    der.hotel_id=#{hotelId}
    and child.order_state=#{orderState}
    and der.check_time BETWEEN
        #{admissionsStartTime} and #{admissionsEndTime}

    and der.check_out_time BETWEEN
        #{departureStartTime} and #{departureEndTime}

    and room.room_name =#{roomName}
    and der.check_type =#{checkType}
    and der.order_placer LIKE '%${orderPlacer}%'
    and der.phone LIKE '%${phone}%'
    and child.room_type_id =#{roomType}
    and room.room_name LIKE '%${roomName}%'
    and der.channel LIKE '%${channel}%'
    and der.OTA LIKE '%${OTA}%'

SELECT COUNT(child.id)
FROM hm_order der LEFT JOIN hm_order_child child ON der.id=child.order_id
INNER JOIN hm_room_type type ON type.id=child.room_type_id LEFT JOIN hm_room room ON room.id=child.room_id

    der.hotel_id=#{hotelId}
    and child.order_state=#{orderState}
    and der.check_time BETWEEN
        #{admissionsStartTime} and #{admissionsEndTime}

    and der.check_out_time BETWEEN
        #{departureStartTime} and #{departureEndTime}

    and room.room_name =#{roomName}
    and der.check_type =#{checkType}
    and der.order_placer LIKE '%${orderPlacer}%'
    and der.phone LIKE '%${phone}%'
    and child.room_type_id =#{roomType}
    and room.room_name LIKE '%${roomName}%'
    and der.channel LIKE '%${channel}%'
    and der.OTA LIKE '%${OTA}%'
